import React, {Component} from 'react';

class Hello extends Component {
    static defaultProps = {
        isSpecial: true,
        name: '이름없음',
    }

    render() {
        const {isSpecial, name, color} = this.props;
        return (
            <div style={color}>
                {isSpecial && <b>*</b>}
                안녕하세요 {this.props.name}
            </div>
        );
    }
}

